{{% macro object_pwhistory_remember(path, test_ref) %}}

  {{{ path }}}
  {{% if product == "rhel8" -%}}
  ^\s*password\s+(?:required)\s+pam_pwhistory\.so.*remember=([0-9]*).*$
  {{%- else -%}}
  ^\s*password\s+(?:requisite)\s+pam_pwhistory\.so.*remember=([0-9]*).*$
  {{%- endif %}}
  1

{{% endmacro %}}

{{{ object_pwhistory_remember( path="/etc/pam.d/system-auth", test_ref="accounts_password_pam_pwhistory_remember_system_auth") }}}
{{{ object_pwhistory_remember( path="/etc/pam.d/password-auth", test_ref="accounts_password_pam_pwhistory_remember_password_auth") }}}
